
public class ifelseif {

	public static void main(String[] args) {
		int marks = 44;
		if (marks>75) {
			System.out.println("A grade");
		}else if (marks>65) {
			System.out.println("B grade");
		}else if (marks>55) {
			System.out.println("C grade");
		}else {
			System.out.println("Fail");
		}
		// TODO Auto-generated method stub

	}

}
